use std::time::Instant;

fn main() {
    // let input = "mjqjpqmgbljsphdztnvjfqwrcgsmlb";
    let input = "";
    let input_bytes = input.to_string();

    // let part_1_length = 4;
    let part_2_length = 14;
    let marker_length = part_2_length;

    let now = Instant::now();

    let mut letter_stack = String::new();
    let mut marker_index = 0;

    for i in 0..input.len() {
        let c = input_bytes.chars().nth(i).unwrap();

        letter_stack.push(c);

        for j in i + 1..i + marker_length {
            let c = input_bytes.chars().nth(j).unwrap();
            if letter_stack.contains(c) {
                letter_stack = String::new();
                break;
            }
            letter_stack.push(c);
        }

        if letter_stack.len() == marker_length {
            marker_index = i + marker_length;
            break;
        }
    }

    // print result
    println!("Result: {} at {}", letter_stack, marker_index);

    println!("time: {}ms", now.elapsed().as_millis());
}
